mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT a.PHONE_NUMBER, fn_DetailCodeNm( a.PHONE_GUBUN) phoneGubunTxt, a.PHONE_USEYN, LOGIN_ID, LOGIN_PASSWORD, a.FRST_REGIST_PNTTM, a.FRST_REGISTER_ID, a.LAST_UPDT_PNTTM, a.LAST_UPDUSR_ID, fn_DetailCodeNm( a.PHONE_STATE) phoneStateTxt
FROM tb_userphoneinfo a WHERE 1=1
and a.PHONE_NUMBER LIKE CONCAT('%',#{searchKeyword},'%')
and a.AGENT_REMARK LIKE CONCAT('%',#{searchKeyword},'%')
and a.PHONE_GUBUN = #{searchPhoneGubun}
and a.PHONE_STATE = #{searchPhoneState}
and a.PHONE_USEYN = #{searchPhoneUseyn}
ORDER BY a.PHONE_NUMBER ASC )TB ORDER BY TB.PHONE_NUMBER DESC limit #{firstIndex}, #{recordCountPerPage}

SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT a.PHONE_NUMBER, a.PHONE_GUBUN, a.PHONE_USEYN, a.PHONE_STATE FROM tb_userphoneinfo WHERE 1=1
and a.PHONE_NUMBER LIKE CONCAT('%',#{searchKeyword},'%')
and a.AGENT_REMARK LIKE CONCAT('%',#{searchKeyword},'%')
and a.PHONE_GUBUN = #{searchPhoneGubun}
and a.PHONE_STATE = #{searchPhoneState}
ORDER BY a.PHONE_NUMBER DESC )TB ORDER BY TB.PHONE_NUMBER DESC limit #{firstIndex}, #{recordCountPerPage}

SELECT ifnull(count(*),0) FROM tb_userphoneinfo WHERE 1=1
and a.PHONE_NUMBER LIKE CONCAT('%',#{searchKeyword},'%')
and a.AGENT_REMARK LIKE CONCAT('%',#{searchKeyword},'%')
and a.PHONE_GUBUN = #{searchPhoneGubun}
and a.PHONE_STATE = #{searchPhoneState}

mapper.update:
UPDATE tb_userphoneinfo SET PHONE_GUBUN =#{phoneGubun},
                            PHONE_USEYN = #{phoneUseyn},
                            PHONE_STATE = #{phoneState},
                            LAST_UPDT_PNTTM = now(),
                            LAST_UPDUSR_ID = #{userId},
                            LOGIN_ID = #{loginId}

                            ,LOGIN_PASSWORD = #{loginPassword}  

WHERE PHONE_NUMBER =#{phoneNumber}
